mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY OBJECTID) RowNumber, 村 as Village, 组 as Community, 所属户 as Household, 联系电话 as Phone, 编号 as FieldNumber, Convert(decimal(38,2),面积_亩) as Acreage, 是否流转 as IsTransfer, 是否林下 as IsForest, 种类 as Breed, Season, 大棚属性 as GreenhouseCount FROM CROP
AND 村 = #{request.village,jdbcType=VARCHAR}
AND 所属户 like '%'+#{request.household,jdbcType=VARCHAR}+'%'
AND 种类 = #{request.breed,jdbcType=VARCHAR}
AND season = #{request.season,jdbcType=VARCHAR}
AND 是否流转 = #{request.isTransfer,jdbcType=VARCHAR}

SELECT COUNT(*) FROM CROP


        AND 村 = #{request.village,jdbcType=VARCHAR}


        AND 所属户 like '%'+#{request.household,jdbcType=VARCHAR}+'%'


        AND 种类 = #{request.breed,jdbcType=VARCHAR}


        AND season = #{request.season,jdbcType=VARCHAR}


        AND 是否流转 = #{request.isTransfer,jdbcType=VARCHAR}

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY OBJECTID) ROWNO, 村 as Village, 组 as Community, 所属户 as Household, 联系电话 as Phone, 编号 as FieldNumber, Convert(decimal(38,2),面积_亩) as Acreage, 是否流转 as IsTransfer, 是否林下 as IsForest, 种类 as Breed, Season, 大棚属性 as GreenhouseCount, OBJECTID FROM CROP
AND 村 = #{request.village,jdbcType=VARCHAR}
AND 所属户 like '%'+#{request.household,jdbcType=VARCHAR}+'%'
AND 种类 = #{request.breed,jdbcType=VARCHAR}
AND season = #{request.season,jdbcType=VARCHAR}
AND 是否流转 = #{request.isTransfer,jdbcType=VARCHAR}
) TABLE_ALIAS WHERE TABLE_ALIAS.ROWNO BETWEEN #{startNo} AND #{endNo}

SELECT COUNT(*) FROM (
    SELECT 村 AS Village, 种类 AS Breed
    FROM CROP WHERE Season = #{season,jdbcType=VARCHAR}
    GROUP BY 村,种类
) TEMP

SELECT *
FROM
(
    SELECT t.*, ROW_NUMBER() OVER(ORDER BY T.Village) RowNumber
    FROM
    (
        SELECT 村 AS Village, 种类 AS Breed, Convert(decimal(38,2),SUM(面积_亩)) AS Acreage
        FROM CROP WHERE Season = #{season,jdbcType=VARCHAR}
        GROUP BY 村,种类
    ) t
) TABLE_ALIAS
WHERE TABLE_ALIAS.RowNumber BETWEEN #{startNo} AND #{endNo}

SELECT COUNT(*) FROM (
    SELECT 所属户 AS Household, 种类 AS Breed
    FROM CROP WHERE Season = #{season,jdbcType=VARCHAR} AND 村 = #{village,jdbcType=VARCHAR}
    GROUP BY 所属户,种类
) TEMP

SELECT *
FROM
(
    SELECT t.*, ROW_NUMBER() OVER(ORDER BY T.Household) RowNumber
    FROM
    (
        SELECT 所属户 AS Household, 种类 AS Breed, Convert(decimal(38,2),SUM(面积_亩)) AS Acreage
        FROM CROP WHERE Season = #{season,jdbcType=VARCHAR} AND 村 = #{village,jdbcType=VARCHAR}
        GROUP BY 所属户,种类
    ) t
) TABLE_ALIAS
WHERE TABLE_ALIAS.RowNumber BETWEEN #{startNo} AND #{endNo}

SELECT CropAttribute, LayerAttribute, Convert(decimal(38,2), SUM(area)) AS Area FROM ( select s.${request.cropAttribute} as CropAttribute, t.${request.layerAttribute} as LayerAttribute, s.Shape.STIntersection(t.Shape).STArea() as area FROM CROP s inner join ${request.layerName} t on s.Shape.STIntersects(t.Shape) = 1 WHERE s.Season=#{request.season} and s.UUID IN
#{item}
) temp GROUP BY CropAttribute, LayerAttribute

SELECT *
FROM
(
    SELECT t.*, ROW_NUMBER() OVER(ORDER BY t.FeedbackTime) RowNumber
    FROM
    (
        SELECT OBJECTID, Feedback, FeedbackUser, FeedbackTime, Status, ProcessResult, ProcessUser, ProcessTime
        FROM FEEDBACK


                Status = #{status,jdbcType=VARCHAR}


    ) t
) TABLE_ALIAS
WHERE TABLE_ALIAS.RowNumber BETWEEN #{startNo} AND #{endNo}
